# The Result class represents the result of the engine execution
class Result
  # @param [Array<Event>] The events generated by the rule engine execution
  def initialize(events)
    @events = events
  end

  # @return [Price] the discount associated to the result of engine execution
  def as_discount
    @events.map(&:discount).inject do |total, discount|
      total.sum(discount)
    end || Price.zero
  end
end
